import React, { useState, useContext } from 'react';
import { StyleSheet, Animated, Text, View } from 'react-native';
import SessionContext from '../Contexts.jsx';
import ActionButton from './ActionButton.jsx';
import { CountdownCircleTimer } from 'react-native-countdown-circle-timer';

export default function Timer() {
  const { inSession, setSession, time } = useContext(SessionContext);
  const [isPlaying, setIsPlaying] = useState(false);

  const timeConvert = (remainingTime) => {
    let minutes = Math.floor(remainingTime / 60);
    let seconds = remainingTime % 60;

    if (minutes < 10) {
      minutes = `0${minutes}`;
    }

    if (seconds < 10) {
      seconds = `0${seconds}`;
    }

    return `${minutes}:${seconds}`;
  }

  return (
    <View style={style.container}>
      <CountdownCircleTimer
        isPlaying={inSession}
        duration={10}
        colors={[['#A4AA88', 1]]}
      >
        {({ remainingTime, animatedColor }) => (
          <Animated.Text style={style.timerText}>
            {timeConvert(remainingTime)}
            <Text style={style.subText}>remaining</Text>
          </Animated.Text>
        )}
      </CountdownCircleTimer>
      <ActionButton />
    </View>

    // <View style={style.timer}>
    //   <Text style={style.timerText}>{minutes}:{secs}</Text>
    //   <Text style={style.subtext}>minutes</Text>
    // </View>
  )
};

const style = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center'
  },
  timer: {
    flex: 1,
    alignItems: 'center',
    top: '50%',
    marginBottom: '5%'
  },
  timerText: {
    flex: 1,
    flexDirection: 'column',

    color: '#787878',
    fontSize: 40,
  },
  subtext: {
    color: '#787878',
    fontSize: 24,
    fontWeight: '500',
  }
})
