import React, { useState, useContext } from 'react';
import { StyleSheet, Animated, Text, View } from 'react-native';
import SessionContext from '../Contexts.jsx';
import ActionButton from './ActionButton.jsx';
import { CountdownCircleTimer } from 'react-native-countdown-circle-timer';

export default function Timer() {
  const { inSession, setSession, time } = useContext(SessionContext);
  const [isPlaying, setIsPlaying] = useState(false);

  let minutes = Math.floor(time / 60);
  let secs = time % 60;
  if (secs < 10) {
    secs = `0${secs}`;
  }

  return (
    <View>
      <CountdownCircleTimer
        isPlaying={inSession}
        duration={time}
        colors={[['#A4AA88', 1]]}
      >
        {({ remainingTime, animatedColor }) => (
          <Animated.Text style={{ color: animatedColor, fontSize: 60 }}>
            {remainingTime}
          </Animated.Text>
        )}
      </CountdownCircleTimer>
      <ActionButton />
    </View>

    // <View style={style.timer}>
    //   <Text style={style.timerText}>{minutes}:{secs}</Text>
    //   <Text style={style.subtext}>minutes</Text>
    // </View>
  )
};

const style = StyleSheet.create({
  timer: {
    flex: 2,
    alignItems: 'center',
    top: '50%',
    marginBottom: '5%'
  },
  timerText: {
    color: '#787878',
    fontSize: 86,
  },
  subtext: {
    color: '#787878',
    fontSize: 24,
    fontWeight: '500',
  }
})
