import React, { useState, useContext, useEffect, useRef } from 'react';
import { StyleSheet, Animated, Text, View } from 'react-native';
import SessionContext from '../Contexts.jsx';
import ActionButton from './ActionButton.jsx';
import { CountdownCircleTimer } from 'react-native-countdown-circle-timer';

export default function Timer() {
  const { inSession, setSession, time } = useContext(SessionContext);
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const moveAnim = useRef(new Animated.Value(10)).current;
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    Animated.timing(
      fadeAnim,
      {
        toValue: 1,
        duration: 2000
      },
      moveAnim,
      {
        toValue: 20,
        duration: 1000,
      }
    ).start();
  }, [inSession] )

  const timeConvert = (remainingTime) => {
    let minutes = Math.floor(remainingTime / 60);
    let seconds = remainingTime % 60;

    if (minutes < 10) {
      minutes = `0${minutes}`;
    }

    if (seconds < 10) {
      seconds = `0${seconds}`;
    }

    return `${minutes}:${seconds}`;
  }

  return (
    <Animated.View style={
      inSession ? dynamicStyle('20%', fadeAnim).container : dynamicStyle('10%', fadeAnim).container,
      {opacity: fadeAnim}
      }>
      <CountdownCircleTimer
        isPlaying={inSession}
        duration={time}
        colors={[['#A4AA88', 1]]}
        size={300}
      >
        {({ remainingTime, animatedColor }) => (
          <View style={style.textContainer}>
            <Animated.Text style={style.timerText}>
              {timeConvert(remainingTime)}
            </Animated.Text>
            <Text style={style.subtext}>remaining</Text>
          </View>
        )}
      </CountdownCircleTimer>
        <ActionButton />
    </Animated.View>
  )
};

const dynamicStyle = (pos, opacity) => StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    top: pos,
    marginBottom: '20%',
  },
})

const style = StyleSheet.create({
  textContainer: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  timerText: {
    color: '#787878',
    fontSize: 60,
  },
  subtext: {
    color: '#787878',
    fontSize: 18,
    fontWeight: '500',
  }
})
